import ACTIONS from './index'
import axios from 'axios'

export const dispatchLogin = () => {
    return {
        type: ACTIONS.LOGIN
    }
}


export const dispatchGetUser = (response) => {
    return {
        type: ACTIONS.GET_USER,
        payload: {
            user: response.data,
            isAdmin: response.data.role === 1 ? true : false
        }
    }
}
