// ïmport package react
import React, { useState, useEffect } from 'react';
import { Link , useHistory, useLocation } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import decode from 'jwt-decode';
import { toast } from 'react-toast'
import { AUTH } from '../../JS/actionsTypes/actionAuth';
import {useSelector} from 'react-redux'





// import google & github login 
import { GoogleLogin } from 'react-google-login';
import LoginGithub from 'react-login-github';


// import axios
import axios from 'axios';

import { authenticate, isAuth } from '../../helpers/auth';
import {LOGOUT} from '../../JS/actionsTypes/actionAuth';


// import CSS
import { AppBar, Typography, Toolbar, Avatar, Button } from '@material-ui/core'
import './Navbar.css'
import useStyles from './styles';

// import logo
import logo from "../../Assets/logo.png"

const GITHUB_CLIENT_ID = "e8db77544d2e4e9ab741";
const gitHubRedirectURL = "http://localhost:3000/api/githublogin";
const path = "/";
const Navbar =  ()  => {

  //const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));
  const [user, setUser] = useState();

  useEffect(() => {
    (async function () {
      const usr = await axios
        .get(`http://localhost:7000/api//githublogin`, {
          withCredentials: true,
        })
        .then((res) => res.data);

      setUser(usr);
    })();
  }, []);

  //console.log(user)
  const dispatch = useDispatch();
  const location = useLocation();
  const history = useHistory();
  const classes = useStyles();



  // send google token
  const sendGoogleToken = tokenId => {
    axios
      .post(`http://localhost:7000/api/googlelogin`, {
        idToken: tokenId
      })
      .then(res => {
        console.log(res.data);
        history.push('/project')
      })
      .catch(error => {
        console.log('GOOGLE SIGNIN ERROR', error.response);
      });
  };
  const informParent = response => {
    authenticate(response, () => {
      isAuth() && isAuth().role === 'admin'
        ? history.push('/admin')
        : history.push('/private');
    });
  };

// send github token
const sendGithubToken = (client_id, code, client_secret) => {
  axios
    .post(`http://localhost:7000/api/githublogin`, {
      client_id, 
      code,
      client_secret
      
    })
    .then(res => {
      console.log(res.data);
      //informParent(res);
    })
    .catch(error => {
      console.log('GITHUB SIGNIN ERROR', error.response);
    });
};
      // handle google
      const responseGoogle = response => {
        const result = response?.profileObj;
    const token = response?.tokenId;
        console.log(response);
        sendGoogleToken(response.tokenId);
        dispatch({ type: AUTH, data: { result, token } });

      };
      const [username, setUsername] = useState("");

    
      const githubSuccess = async(response) => {
        try{
          sendGithubToken(response.client_secret, response.code, response.client_id)

          const result =  response;
          const token = response?.token;
  
  
         console.log(response);
  
          dispatch({ type: AUTH, data: { result, token } })
          history.push('/project')
        
        }
        catch(err){
          return response.status(500).json({msg: err.message})

        }
   
      }
      const githubFailure = response =>
       console.error(response);
      
       
  const logout = () => {
    localStorage.clear();

    history.push('/');

    setUser(null);
  };


 
    
    return (
        <div>
      <AppBar color="white">
 {/*logo */}
  <img  src={logo} alt="logo" className="logo"></img>

 {/*button signin  logout */}
 <div className='buttons'>

 <Toolbar className={classes.toolbar}>
 {!user ? (
        <a
          href={`https://github.com/login/oauth/authorize?client_id=${GITHUB_CLIENT_ID}&redirect_uri=${gitHubRedirectURL}?path=${path}&scope=user:email`}
        >
          LOGIN WITH GITHUB
        </a>
      ) : (
        <h1>Welcome {user.login}</h1>
      )}
             
      </Toolbar>
        </div> 
       
          


        </AppBar> 

        </div>
    )
}

export default Navbar
