import React from 'react'
import { GoogleLogin } from 'react-google-login';
import { GithubLoginButton } from "react-social-login-buttons";
import GitHubLogin from 'react-github-login';

import axios from 'axios';
import { authenticate, isAuth } from '../../helpers/auth';

const Navbar =  ({ history })  => {

    const sendGoogleToken = tokenId => {
        axios
          .post(`${process.env.REACT_APP_API_URL}/googlelogin`, {
            idToken: tokenId
          })
          .then(res => {
            console.log(res.data);
            informParent(res);
          })
          .catch(error => {
            console.log('GOOGLE SIGNIN ERROR', error.response);
          });
      };

      const sendGithubToken = (userID, accessToken) => {
        axios
          .post(`${process.env.REACT_APP_API_URL}/githublogin`, {
            userID,
            accessToken
          })
          .then(res => {
            console.log(res.data);
            informParent(res);
          })
          .catch(error => {
            console.log('GITHUB SIGNIN ERROR', error.response);
          });
      };

      const informParent = response => {
        authenticate(response, () => {
          isAuth() && isAuth().role === 'admin'
            ? history.push('/admin')
            : history.push('/profile');
        });
      };
      const responseGoogle = response => {
        console.log(response);
        sendGoogleToken(response.tokenId);
      };

 
const responseGithub = response => {
  console.log(response);
  sendGithubToken(response.userID, response.accessToken)
};
    return (
        <div>
           <GoogleLogin
                  clientId={`${process.env.REACT_APP_GOOGLE_CLIENT}`}
                  buttonText="Login with Google"
                  onSuccess={responseGoogle}
                  onFailure={responseGoogle}
                  cookiePolicy={'single_host_origin'}
            
                   
                ></GoogleLogin>

<GitHubLogin clientId={`${process.env.REACT_APP_GITHUB_CLIENT}`}
    onSuccess={responseGithub}
    onFailure={responseGithub}/>
   

        </div>
    )
}

export default Navbar
