import {LOGIN,GET_USER} from '../actions/'

const initialState = {
    user: [],
    isLogged: false,
    isAdmin: false
}

const authGithubReducer = (state = initialState, { type, payload }) => {
    switch(type){
        case LOGIN:
            localStorage.setItem("token", payload.token);

            return {
                ...state,
                isLogged: true
            }
        case GET_USER:
            return {
                ...state,
                user: payload.user,
                isAdmin: payload.isAdmin
            }
        default:
            return state
    }
}

export default authGithubReducer
