// Ã¯mport package react
import React, { useState, useEffect } from 'react';
import { Link , useHistory, useLocation } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import decode from 'jwt-decode';
import { toast } from 'react-toast'




// import google & github login 
import { GoogleLogin } from 'react-google-login';
import LoginGithub from 'react-login-github';


// import axios
import axios from 'axios';

import { authenticate, isAuth } from '../../helpers/auth';
import {LOGOUT} from '../../JS/actionsTypes/actionAuth';


// import CSS
import { AppBar, Typography,  IconButton, Avatar, Button, Menu, MenuItem } from '@material-ui/core';
import './Navbar.css'
import useStyles from './styles';

// import logo
import logo from "../../Assets/logo.png"

const Navbar =  ()  => {

  const dispatch = useDispatch();
  const location = useLocation();
  const history = useHistory();
  const classes = useStyles();



  // send google token
    const sendGoogleToken = tokenId => {
        axios
          .post(`${process.env.REACT_APP_API_URL}/googlelogin`, {
            idToken: tokenId
          })
          .then(res => {
            console.log(res.data);
            //informParent(res);
            history.push('/project');

          })
          .catch(error => {
            toast.error('GOOGLE SIGNIN ERROR', error.response);
          });
      };

// send github token
      const sendGithubToken = (clientID, requestToken, clientSecret) => {
        axios
          .post(`${process.env.REACT_APP_API_URL}/githublogin`, {
            clientID, 
            clientSecret,
            requestToken,
            
          })
          .then(res => {
            console.log(res.data);
            informParent(res);
          })
          .catch(error => {
            console.log('GITHUB SIGNIN ERROR', error.response);
          });
      };

      const informParent = response => {
        authenticate(response, () => {
          isAuth() && isAuth().role === 'admin'
            ? history.push('/admin')
            : history.push('/profile');
        });
      };

      // handle google
      const responseGoogle = response => {
        console.log(response);
        sendGoogleToken(response.tokenId);
      };

      const githubSuccess = response => {
        console.log(response);

        sendGithubToken(response.clientID, response.requestToken, response.clientSecret)

      }
      const githubFailure = response =>
       console.error(response);

  // Logout
  const logout = () => {
    dispatch( logout());

    history.push('/');

    setUser(null);
  };
 
   // useeffect    
  useEffect(() => {
    const token = user?.token;

    if (token) {
      const decodedToken = decode(token);

      if (decodedToken.exp * 1000 < new Date().getTime()) logout();
    }

    setUser(JSON.parse(localStorage.getItem('profile')));
  }, [location]);

    return (
        <div>
      <AppBar color="white">
 {/*logo */}
  <img  src={logo} alt="logo" className="logo"></img>

 {/*button signin  logout */}
 <div className='buttons'>

 {user?.result ? (
          <div className={classes.profile}>
            <Avatar className={classes.purple} alt={user?.result.name} src={user?.result.imageUrl}>{user?.result.name.charAt(0)}</Avatar>
            <Typography className={classes.userName} variant="h6">{user?.result.name}</Typography>
            <Button variant="contained" className={classes.logout} color="secondary" onClick={logout}>Logout</Button>
          </div>
        ) : (
          <GoogleLogin
          clientId={`${process.env.REACT_APP_GOOGLE_CLIENT}`}
          buttonText="Login"
          onSuccess={responseGoogle}
          onFailure={responseGoogle}
          cookiePolicy={'single_host_origin'}
        />   
             )}
            
        </div> 
       
           {/* <LoginGithub
                  clientId={`${process.env.REACT_APP_GITHUB_CLIENT}`}
                  buttonText="Login with Github"
                  onSuccess={githubSuccess}
                  onFailure={githubFailure}
                  className="icon-github"
            ></LoginGithub>*/}


        </AppBar> 

        </div>
    )
}

export default Navbar
