import { Route, Switch } from "react-router-dom";
import {dispatchLogin, fetchUser, dispatchGetUser} from './JS/actions/actionAuth'
import {useDispatch, useSelector} from 'react-redux'
import React, {useEffect} from 'react';
import axios from 'axios'



// import componenets
import Navbar from './Components/Navbar/Navbar'
import Nav from './Components/Navbar/Nav'

import Footer from "./Components/Footer/Footer";
import AddEditProject from "./Components/AddEditProject/AddEditProject";



// import pages
import Home from './Pages/Home/Home';
import Profile from './Pages/Profile/Profile';
import Project from "./Pages/Project/Project";
import Errors from "./Pages/Errors/Errors";
import auth from "./Pages/auth/auth";




// css
import './App.css';
import Header from "./Components/Navbar/Header";




function App() {
  const dispatch = useDispatch()
  const token = useSelector(state => state.token)
  const auth = useSelector(state => state.auth)

  useEffect(() => {
    const firstLogin = localStorage.getItem('firstLogin')
    if(firstLogin){
      const getToken = async () => {
        const res = await axios.post('http://localhost:7000/api/refresh_token', null)
        dispatch({type: 'GET_TOKEN', payload: res.data.access_token})
      }
      getToken()
    }
  },[auth.isLogged, dispatch])

  useEffect(() => {
    if(token){
      const getUser = () => {
        dispatch(dispatchLogin())

        return fetchUser(token).then(res => {
          dispatch(dispatchGetUser(res))
        })
      }
      getUser()
    }
  },[token, dispatch])



  return (
    <div>
      <Navbar/>

      <Switch>

      <Route path="/" exact component={Home} />

      <Route path="/profile" component={Profile} />



      <Route path="/project" component={Project} />

      <Route path="/(add_project|edit_project)" component={AddEditProject} /> 

      <Route path="/*" component={Errors} />
      </Switch>

  <Footer />      
    </div>


  )
}
export default App;
