// ïmport package react
import React, { useState, useEffect } from 'react';
import { Link , useHistory, useLocation } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import decode from 'jwt-decode';
import { toast } from 'react-toast'
import { AUTH } from '../../JS/actionsTypes/actionAuth';
import {useSelector} from 'react-redux'



import {dispatchLogin, dispatchGetUser} from '../../JS/actions/actionAuth'


// import google & github login 
import { GoogleLogin } from 'react-google-login';
import LoginGithub from 'react-login-github';


// import axios
import axios from 'axios';

import { authenticate, isAuth } from '../../helpers/auth';
import {LOGOUT} from '../../JS/actionsTypes/actionAuth';


function Header() {
    const auth = useSelector(state => state.auth)
    const [user, setUser] = useState({});


    //const {user, isLogged} = auth
  //console.log(user)
  const dispatch = useDispatch();
  const location = useLocation();
  const history = useHistory();

    const handleLogout = async () => {
        try {
            await axios.get('/user/logout')
            localStorage.removeItem('firstLogin')
            window.location.href = "/";
        } catch (err) {
            window.location.href = "/";
        }
    }
// handle Github
const githubSuccess = async(response) => {
    try{
        const { client_secret, code, client_id} = response
        const res = await axios.post(`http://localhost:7000/api/githublogin`, 
        {client_secret, code, client_id})
      //sendGithubToken(response.client_secret, response.code, response.client_id)
console.log(res)
      const result = res;
      console.log(result)


      const token = response?.token;



      //dispatch({ type: AUTH, data: { result, token } })
      //setUser(result);
      //dispatch(dispatchLogin())
      dispatch(dispatchGetUser(res))
    
      dispatch({ type: AUTH, data: { result, token } })

      setUser({...user, error:'', success: res.data.msg})
      console.log(user)


      history.push('/project')
    
    }
    catch (err) {
      err.response.data.msg && 
      setUser({...user, err: err.response.data.msg, success: ''})
  }
  }
  const githubFailure = response =>
  alert('Github Sign In was unsuccessful. Try again later');
  
    const userLink = () => {
        return <li className="drop-nav">
            <Link to="#" className="avatar">
            <img src={user.avatar} alt=""/> {user.name} <i className="fas fa-angle-down"></i>
            </Link>
            <ul className="dropdown">
                <li><Link to="/profile">Profile</Link></li>
                <li><Link to="/" onClick={handleLogout}>Logout</Link></li>
            </ul>
        </li>
    }

    const transForm = {
        transform: isLogged ? "translateY(-5px)" : 0
    }

    return (
        <header>
            <div className="logo">
                <h1><Link to="/">DevAT✮Shop</Link></h1>
            </div>

            <ul style={transForm}>
                <li><Link to="/"><i className="fas fa-shopping-cart"></i> Cart</Link></li>
                {
                    isLogged
                    ? userLink()
                    :<li>
                    <Link to="/project"><i className="fas fa-user"> 
                    <LoginGithub
                  clientId={`e8db77544d2e4e9ab741`}
                  buttonText="Login with Github"
                  onSuccess={githubSuccess}
                  onFailure={githubFailure}
                
             ></LoginGithub></i> 
                    </Link>
                    </li>
                }
                
            </ul>
        </header>
    )
}

export default Header
