import ACTIONS from '../actions/'

const access_token = ''

const tokenReducer = (state = access_token, action) => {
    switch(action.type){
        case ACTIONS.GET_TOKEN:
            return action.payload
        default:
            return state
    }
}

export default tokenReducer
