// ïmport package react
import React, { useState, useEffect } from 'react';
import { Link , useHistory, useLocation } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import decode from 'jwt-decode';
import { toast } from 'react-toast'
import { AUTH } from '../../JS/actionsTypes/actionAuth';
import {useSelector} from 'react-redux'





// import google & github login 
import { GoogleLogin } from 'react-google-login';
import LoginGithub from 'react-login-github';


// import axios
import axios from 'axios';

import { authenticate, isAuth } from '../../helpers/auth';
import {LOGOUT} from '../../JS/actionsTypes/actionAuth';

import {dispatchLogin, dispatchGetUser} from '../../JS/actions/actionAuth'



// import CSS
import { AppBar, Typography, Toolbar, Avatar, Button } from '@material-ui/core'
import './Navbar.css'
import useStyles from './styles';

// import logo
import logo from "../../Assets/logo.png"


const Navbar =  ()  => {
  const [user, setUser] = useState({ email: '',
  password: '',
  err: '',
  success: ''});  
  console.log(user)

  //const usr = useSelector((state) => state.authGithubReducer.user);

  //console.log(user)
  const dispatch = useDispatch();
  const location = useLocation();
  const history = useHistory();
  const classes = useStyles();



  // send google token
  const sendGoogleToken = tokenId => {
    axios
      .post(`http://localhost:7000/api/googlelogin`, {
        idToken: tokenId
      })
      .then(res => {
        console.log(res.data);
        history.push('/project')
      })
      .catch(error => {
        console.log('GOOGLE SIGNIN ERROR', error.response);
      });
  };
  const informParent = response => {
    authenticate(response, () => {
      isAuth() && isAuth().role === 'admin'
        ? history.push('/admin')
        : history.push('/private');
    });
  };

// send github token
const sendGithubToken = (client_id, code, client_secret) => {
  axios
    .post(`http://localhost:7000/api/githublogin`, {
      client_id, 
      code,
      client_secret
      
    })
    .then(res => {
      console.log(res.data);
      //informParent(res);
    })
    .catch(error => {
      console.log('GITHUB SIGNIN ERROR', error.response);
    });
};
      // handle google
      const responseGoogle = response => {
        const result = response?.profileObj;
    const token = response?.tokenId;
        console.log(response);
        sendGoogleToken(response.tokenId);
        dispatch({ type: AUTH, data: { result, token } });

      };

     const responseGoogleFailure  = () => 
     alert('Google Sign In was unsuccessful. Try again later');


     // handle Github
      const githubSuccess = async(response) => {
        try{
            const { client_secret, code, client_id} = response
            const res = await axios.post(`http://localhost:7000/api/githublogin`, 
            {client_secret, code, client_id})
          //sendGithubToken(response.client_secret, response.code, response.client_id)
console.log(res)
          const result = res;
          //console.log(result)

  
          const token = response?.token;
         
          setUser({...user, err: '', success: res.data.msg})
  console.log(user)
  
  
          //dispatch({ type: AUTH, data: { result, token } })
          //setUser(result);
          //dispatch(dispatchLogin())
          //dispatch(dispatchGetUser(res))
        
          dispatch({ type: AUTH, data: { res, token } })
          history.push('/project')
        
        }
        catch (err) {
          err.response.data.msg && 
          setUser({...user, err: err.response.data.msg, success: ''})
      }
      }
      const githubFailure = response =>
      alert('Github Sign In was unsuccessful. Try again later');
      
       
  const logout = () => {
    localStorage.clear();

    history.push('/');

    setUser(null);
  };

// useEffect
  useEffect(() => {
    const token = user?.token;

    if (token) {
      const decodedToken = decode(token);

      if (decodedToken.exp * 1000 < new Date().getTime()) logout();
    }

    //setUser(JSON.parse(localStorage.getItem('profile')));
  }, [location]);

    return (
        <div>
          
      <AppBar color="white">
 {/*logo */}
  <img  src={logo} alt="logo" className="logo"></img>
 {/*button signin  logout */}
 <div className='buttons'>
 <Toolbar className={classes.toolbar}>
        {user?(
          <div className={classes.profile}>
            <Avatar className={classes.purple} alt={user?.name} src={user?.imageUrl}>{user?.name?.charAt(0) || "UU"}</Avatar>
            <Typography className={classes.userName} variant="h6">{user?.name}</Typography>
            <Button variant="contained" className={classes.logout} color="secondary" onClick={logout}>Logout</Button>
          </div>
        ) : (
          <Button component={Link} to="/project" variant="contained" color="primary">
          <LoginGithub
                  clientId={`e8db77544d2e4e9ab741`}
                  buttonText="Login with Github"
                  onSuccess={githubSuccess}
                  onFailure={githubFailure}
                  className="icon-github"
                
           ></LoginGithub>
          </Button>
        )}
      </Toolbar>
        </div> 
       
          


        </AppBar> 

        </div>
    )
}

export default Navbar
